    "\n  *[_type == \"shopBanner\" && isActive == true && placement match \"promo-*\"] {\n    _id,\n    title,\n    description,\n    image,\n    ctaText,\n    ctaLink,\n    placement\n  }\n": PROMO_GRID_BANNERS_QUERYResult;
